  <v-container fluid grid-list-md class="contenido">
    <v-layout wrap>
      <!-- RERPORTE INGRESOS APROBADA-->
      <v-flex xs12 sm6 md3>
        <v-hover>
          <v-card @click="dialogo_ingreso()" slot-scope="{ hover }"  
                :class="`elevation-${hover ? 12 : 2}`" >
            <div class="imagen" @click="dialogo_ingreso()">
              <img 
                src="data:image/png;base64,"
              />
            </div>

            <v-card-title primary-title>
              <div class="texto">
                <h3 class="headline mb-0">Ingreso de inventario</h3>
                <div>
                  Se muestra el listado de ingresos aprobadas en un rango de
                  fecha
                </div>
              </div>
            </v-card-title>

            <v-card-actions>
              <v-dialog
                v-model="ingre"
                scrollable
                max-width="800px"
                min-width="800px"
                max-height="500px"
                min-height="500px"
              >
                <v-card>
                  <v-card-text>
                    <v-btn @click="crearPDFAutotableIngreso()"
                      ><v-icon>print</v-icon>
                    </v-btn>
                    <v-layout>
                      <v-text-field
                        type="date"
                        label="Desde"
                        v-model="desdeCA"
                      ></v-text-field>
                      <v-text-field
                        type="date"
                        label="Hasta"
                        v-model="hastaCA"
                      ></v-text-field>
                      <v-btn text @click="ReporteIngreso()">Generar</v-btn>
                    </v-layout>
                    <div id="principal" class="RecuperacionQuincena">
                      <header class="cabecera">
                        <h1>INGRESOS Inventario</h1>
                        <h2>FARMACIA ESCORCIA</h2>
                        <div class="caja">
                          <h3>
                            Hasta: <strong>{{ hastaCA }}</strong>
                          </h3>
                          <h3>
                            Desde: <strong>{{ desdeCA }}</strong>
                          </h3>
                        </div>
                      </header>
                      <section>
                        <table class="tabla">
                          <tr>
                            <th>Id ingreso</th>
                            <th>Código articulo</th>
                            <th>Articulo</th>
                            <th>Proveedor</th>
                            <th>Cantidad</th>
                            <th>Precio</th>
                            <th>Total</th>
                            <th>Fecha</th>
                          </tr>

                          <tr v-for="x in reporteIngreso" :key="x.id">
                            <th v-text="x.id_ingreso"></th>
                            <th v-text="x.codigo_arti"></th>
                            <th v-text="x.nom_arti"></th>
                            <th v-text="x.proveedor"></th>
                            <th v-text="x.cantidad"></th>  
                            <th v-text="x.precio"></th>  
                            <th v-text="x.valor"></th>
                            <th v-text="x.fecha"></th>
                          </tr>
                        </table>

                        <v-btn
                          @click="ingre = false"
                          color="blue darken-1"
                          text
                          class="btn-color"
                          >Cancelar</v-btn
                        >
                      </section>
                    </div>
                  </v-card-text>
                </v-card>
              </v-dialog>
            </v-card-actions>
          </v-card>
        </v-hover>
      </v-flex>

      <!-- RERPORTE INGRESOS APROBADA-->
      <v-flex xs12 sm6 md3>
        <v-hover>
          <v-card @click="dialogo_egreso()" slot-scope="{ hover }"  
                :class="`elevation-${hover ? 12 : 2}`" >
            <div class="imagen" @click="dialogo_egreso()">
              <img 
                src="data:image/png;base64,"
              />
            </div>

            <v-card-title primary-title>
              <div class="texto">
                <h3 class="headline mb-0">Ventas</h3>
                <div>
                  Se muestra el listado de egresos aprobadas en un rango de
                  fecha
                </div>
              </div>
            </v-card-title>

            <v-card-actions>
              <v-dialog
                v-model="egreso"
                scrollable
                max-width="800px"
                min-width="800px"
                max-height="500px"
                min-height="500px"
              >
                <v-card>
                  <v-card-text>
                    <v-btn @click="crearPDFAutotableEgreso()"
                      ><v-icon>print</v-icon>
                    </v-btn>
                    <v-layout>
                      <v-text-field
                        type="date"
                        label="Desde"
                        v-model="desdeE"
                      ></v-text-field>
                      <v-text-field
                        type="date"
                        label="Hasta"
                        v-model="hastaE"
                      ></v-text-field>
                      <v-btn text @click="ReporteEgreso()">Generar</v-btn>
                    </v-layout>
                    <div id="principal" class="RecuperacionQuincena">
                      <header class="cabecera">
                        <h1>VENTAS APROBADA</h1>
                        <h2>FARMACIA ESCORCIA</h2>
                        <div class="caja">
                          <h3>
                            Hasta: <strong>{{ hastaCA }}</strong>
                          </h3>
                          <h3>
                            Desde: <strong>{{ desdeCA }}</strong>
                          </h3>
                        </div>
                      </header>
                      <section>
                        <table class="tabla">
                          <tr>
                            <th>Id egreso</th>
                            <th>Código articulo</th>
                            <th>Articulo</th>
                            <th>Cliente</th>
                            <th>Cantidad</th>
                            <th>Precio</th>
                            <th>Total</th>
                            <th>Fecha</th>
                          </tr>

                          <tr v-for="x in reporteEgreso" :key="x.id">
                            <th v-text="x.id_venta"></th>
                            <th v-text="x.codigo_arti"></th>
                            <th v-text="x.nom_arti"></th>
                            <th v-text="x.cliente"></th>
                            <th v-text="x.cantidad"></th>  
                            <th v-text="x.precio"></th>  
                            <th v-text="x.valor"></th>
                            <th v-text="x.fecha"></th>
                          </tr>
                        </table>

                        <v-btn
                          @click="egreso = false"
                          color="blue darken-1"
                          text
                          class="btn-color"
                          >Cancelar</v-btn
                        >
                      </section>
                    </div>
                  </v-card-text>
                </v-card>
              </v-dialog>
            </v-card-actions>
          </v-card>
        </v-hover>
      </v-flex>

    </v-layout>

    <!-- PANTALLA DE CARGA-->
    <PantallaCarga :activo="carga" @escucharResultPantalla="resultPantalla()"> </PantallaCarga>
        <!-- DIALOGO DE ERROR-->
    <Mensaje :activo="activarError" :msj="msjError" :tipo="tipoMsj" @escucharResultMsj="resultMsj()"> </Mensaje>
    <!--- CIERRE DE SECION --->
    <Secion :activo="login_" @escucharResult="resultHijo()" ></Secion>
  </v-container>
</template>

<script>
import axios from "axios";
import jsPDF from "jspdf";
import autoTable from "jspdf-autotable";
import Secion from '@/components/Secion';
import Mensaje from '@/components/Mensaje';
import PantallaCarga from '@/components/PantallaCarga';

export default {
  data() {
    return {
      carga :false,
      //Ingreso
      ingre:false,
      desdeCA: "",
      hastaCA: "",
      reporteIngreso: [],
      //Ingreso
      egreso: false,
      desdeE: "",
      hastaE: "",
      reporteEgreso: [],
      //secion
      login_:false,
      tipoMsj:'',
      msjError:'',
      activarError:false,
      

    
    };
  },
  components:{
      Secion,
      Mensaje,
      PantallaCarga
    },
  created() {
  },
  methods: {
      //SECION
        resultHijo(){
            this.login_ = false;
        },
        resultMsj(){
            this.activarError = false; 
        },  
        resultPantalla(){
            this.carga = false;
        },
        activarErrores(tipo,err,color){
            if(tipo == 1){
                this.login_ =true;
            }else{
                this.activarError=true;
                this.msjError = err;
                this.tipoMsj = color;
                setTimeout(this.resultMsj,2000);
            }
        },
        //FIN - SECION

        dialogo_ingreso(){
            this.ingre = true;
        },
        dialogo_egreso(){
            this.egreso = true;
        },
        formatMoneda(mon){
            const formatter = new Intl.NumberFormat('en-US', {
            style: 'currency',
            currency: 'USD',
            minimumFractionDigits: 2
            })

            var cordoba  = formatter.format(mon);

            return 'C'+cordoba;
        },
        ReporteIngreso() {
            if (!this.desdeCA || !this.hastaCA || this.desdeCA > this.hastaCA) {
            this.activarErrores(2,"Verificar campos, no puede estar vacio y desde no puede ser mayor a hasta.","red"); //error
            } else {
            this.carga = true; //pantalla de carga 
            let me = this;
            let header = { Authorization: "Bearer " + this.$store.state.token };
            let configuracion = { headers: header };
            axios
                .get(
                "api/ingresos/IngresosXFecha/" +
                    me.desdeCA +
                    "/" +
                    me.hastaCA,
                configuracion
                )
                .then(function (response) {
                me.reporteIngreso = response.data;
                me.resultPantalla(); //Cierre de pantalla
                }).catch(err => {
                        me.resultPantalla() //Cierre de pantalla
                        if (err.response.status==401){
                            me.activarErrores(1);
                        }
                        else if (err.response.status == 403){
                            me.activarErrores(2,"Error de permisos.","orange"); 
                        }
                        else{
                            me.activarErrores(2,err.response.data,"red");
                        }
                        console.log(err.response);
                    });
            }
        },
        ReporteEgreso() {
            if (!this.desdeE || !this.hastaE || this.desdeE > this.hastaE) {
            this.activarErrores(2,"Verificar campos, no puede estar vacio y desde no puede ser mayor a hasta.","red"); //error
            } else {
            this.carga = true; //pantalla de carga 
            let me = this;
            let header = { Authorization: "Bearer " + this.$store.state.token };
            let configuracion = { headers: header };
            axios
                .get(
                "api/Ventas/SalidasXFecha/" +
                    me.desdeE +
                    "/" +
                    me.hastaE,
                configuracion
                )
                .then(function (response) {
                me.reporteEgreso = response.data;
                me.resultPantalla(); //Cierre de pantalla
                }).catch(err => {
                        me.resultPantalla() //Cierre de pantalla
                        if (err.response.status==401){
                            me.activarErrores(1);
                        }
                        else if (err.response.status == 403){
                            me.activarErrores(2,"Error de permisos.","orange"); 
                        }
                        else{
                            me.activarErrores(2,err.response.data,"red");
                        }
                        console.log(err.response);
                    });
            }
        },
        //Metodos que generan los reportes
        crearPDFAutotableIngreso() {
        if (
            !this.desdeCA ||
            !this.hastaCA ||
            this.reporteIngreso.length <= 0
        ) {
            this.activarErrores(2,"Reporte Vacio","red"); //error
            return;
        }

        var desde = this.desdeCA;
        var hasta = this.hastaCA;

        var total = 0;

        this.reporteIngreso.forEach(element => {
            total += element.valor ;
        });

        var result_total = this.formatMoneda(total);

        var columns = [
            { title: "Id Ingreso", dataKey: "id_ingreso" },
            { title: "Código", dataKey: "codigo_arti" },
            { title: "Articulo", dataKey: "nom_arti" },
            { title: "Proveedor", dataKey: "proveedor" },
            { title: "Cantidad", dataKey: "cantidad" },
            { title: "Precio", dataKey: "precio" },
            { title: "Total", dataKey: "valor" },
            { title: "Fecha", dataKey: "fecha" },
        ];
        var rows = [];

        this.reporteIngreso.map(function (x) {
            rows.push({
            id_ingreso : x.id_ingreso,
            codigo_arti: x.codigo_arti,
            nom_arti: x.nom_arti,
            proveedor: x.proveedor,
            cantidad: x.cantidad,
            precio: 'C$'+ x.precio,
            fecha: x.fecha,
            valor :'C$'+ x.valor
            });
        });

        // Only pt supported (not mm or in)
        var doc = new jsPDF("l");
        doc.setFontSize(20);
        doc.setFont('Roboto-Regular', 'bold');
        var titulo = "INGRESOS";
        var subTitulo= "Farmacia Escorcia";
        //TEXTO
        doc.setTextColor(12, 34, 82);
        
        // doc.setFontType(bold);
        doc.text(titulo, 120, 20);
        doc.setFontSize(18);
        doc.text(subTitulo,115,35)

        doc.setFontSize(10);
        //fecha
        doc.text("Desde:", 20, 45);
        doc.text(desde, 32, 45);
        doc.text("Hasta:", 55, 45);
        doc.text(hasta, 67, 45);

        doc.autoTable(columns, rows, {
            startY: 60,
             horizontalPageBreak: true,
        });

        doc.setFontSize(12);
        doc.text("Total : ", 200,45);
        doc.text(result_total, 215, 45);

    
        doc.save('Ingresos por fecha.pdf');

        },
        crearPDFAutotableEgreso() {
        if (
            !this.desdeE ||
            !this.hastaE ||
            this.reporteEgreso.length <= 0
        ) {
            this.activarErrores(2,"Reporte Vacio","red"); //error
            return;
        }

        var desde = this.desdeE;
        var hasta = this.desdeE;

        var total = 0;

        this.reporteEgreso.forEach(element => {
            total += element.valor;
        });

        var result_total = this.formatMoneda(total);

        var columns = [
            { title: "Id venta", dataKey: "id_venta" },
            { title: "Código", dataKey: "codigo_arti" },
            { title: "Articulo", dataKey: "nom_arti" },
            { title: "Cliente", dataKey: "cliente" },
            { title: "Cantidad", dataKey: "cantidad" },
            { title: "Precio", dataKey: "precio" },
            { title: "Total", dataKey: "valor" },
            { title: "Fecha", dataKey: "fecha" },
        ];
        var rows = [];

        this.reporteEgreso.map(function (x) {
            rows.push({
            id_venta : x.id_venta,
            codigo_arti: x.codigo_arti,
            nom_arti: x.nom_arti,
            cliente: x.cliente,
            cantidad: x.cantidad,
            precio: 'C$'+ x.precio,
            fecha: x.fecha,
            valor :'C$'+ x.valor
            });
        });

        // Only pt supported (not mm or in)
        var doc = new jsPDF("l");
        doc.setFontSize(20);
        doc.setFont('Roboto-Regular', 'bold');
        var titulo = "VENTAS";
        var subTitulo= "Farmacia Escorcia";
        //TEXTO
        doc.setTextColor(12, 34, 82);
        
        // doc.setFontType(bold);
        doc.text(titulo, 120, 20);
        doc.setFontSize(18);
        doc.text(subTitulo,110,35)

        doc.setFontSize(10);
        //fecha
        doc.text("Desde:", 20, 45);
        doc.text(desde, 32, 45);
        doc.text("Hasta:", 55, 45);
        doc.text(hasta, 67, 45);

        doc.autoTable(columns, rows, {
            startY: 60,
             horizontalPageBreak: true,
        });

        doc.setFontSize(12);
        doc.text("Total : ", 200,45);
        doc.text(result_total, 215, 45);

    
        doc.save('Egresos por fecha.pdf');

        }
  },
};
</script>

